#!/bin/bash

echo 'Duncan wrote this script for his own use' >&2
echo 'read the whole thing and make appropriate modifications before using it' >&2
echo 'this has only been tested on Arbitrum Sepolia' >&2
exit 1

## POSIX Bash implementation of realpath
## Copied and modified from https://github.com/mkropat/sh-realpath and https://github.com/AsymLabs/realpath-lib/
## Copyright (c) 2014 Michael Kropat - MIT License
## Copyright (c) 2013 Asymmetry Laboratories - MIT License

realpath() {
    _resolve_symlinks "$(_canonicalize "$1")"
}

_directory() {
    local out slsh
    slsh=/
    out="$1"
    out="${out//$slsh$slsh/$slsh}"
    if [ "$out" = / ]; then
        echo /
        return
    fi
    out="${out%/}"
    case "$out" in
        */*)
            out="${out%/*}"
        ;;
        *)
            out=.
        ;;
    esac
    if [ "$out" ]; then
        printf '%s\n' "$out"
    else
        echo /
    fi
}

_file() {
    local out slsh
    slsh=/
    out="$1"
    out="${out//$slsh$slsh/$slsh}"
    if [ "$out" = / ]; then
        echo /
        return
    fi
    out="${out%/}"
    out="${out##*/}"
    printf '%s\n' "$out"
}

_resolve_symlinks() {
    local path pattern context
    while [ -L "$1" ]; do
        context="$(_directory "$1")"
        path="$(POSIXLY_CORRECT=y ls -ld -- "$1" 2>/dev/null)"
        pattern='*'"$(_escape "$1")"' -> '
        path="${path#$pattern}"
        set -- "$(_canonicalize "$(_prepend_context "$context" "$path")")" "$@"
        _assert_no_path_cycles "$@" || return 1
    done
    printf '%s\n' "$1"
}

_escape() {
    local out
    out=''
    local -i i
    for ((i=0; i < ${#1}; i+=1)); do
        out+='\'"${1:$i:1}"
    done
    printf '%s\n' "$out"
}

_prepend_context() {
    if [ "$1" = . ]; then
        printf '%s\n' "$2"
    else
        case "$2" in
            /* ) printf '%s\n' "$2" ;;
             * ) printf '%s\n' "$1/$2" ;;
        esac
    fi
}

_assert_no_path_cycles() {
    local target path

    if [ $# -gt 16 ]; then
        return 1
    fi

    target="$1"
    shift

    for path in "$@"; do
        if [ "$path" = "$target" ]; then
            return 1
        fi
    done
}

_canonicalize() {
    local d f
    if [ -d "$1" ]; then
        (CDPATH= cd -P "$1" 2>/dev/null && pwd -P)
    else
        d="$(_directory "$1")"
        f="$(_file "$1")"
        (CDPATH= cd -P "$d" 2>/dev/null && printf '%s/%s\n' "$(pwd -P)" "$f")
    fi
}

## end POSIX Bash implementation of realpath

set -Eeufo pipefail -o posix

declare project_root
project_root="$(_directory "$(_directory "$(realpath "${BASH_SOURCE[0]}")")")"
declare -r project_root
cd "$project_root"

. "$project_root"/sh/common.sh

declare -r singleton_init=''
declare -r factory_init=''
declare -r fallback_init=''
declare -r multicall_init=''

declare -r deployer='0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7'
declare -r deployer_hash='0x2fa86add0aed31f33a762c9d88e807c475bd51d0f52bd0955754b2608f7e4989'
declare -r singleton_hash='0x21842597390c4c6e3c1239e434a682b054bd9548eee5e9b1d6a4482731023c0f'
declare -r factory_hash='0x337d7f54be11b6ed55fef7b667ea5488db53db8320a05d1146aa4bd169a39a9b'
declare -r fallback_hash='0x03e69f7ce809e81687c69b19a7d7cca45b6d551ffdec73d9bb87178476de1abf'
declare -r multicall_hash='0xa9865ac2d9c7a1591619b188c4d88167b50df6cc0c5327fcbd1c8c75f7c066ad'

declare rpc_url
rpc_url="$(get_api_secret rpcUrl)"
declare -r rpc_url

if [[ ${rpc_url:-unset} = 'unset' ]] ; then
    echo '`rpcUrl` is unset in `api_secrets.json` for chain "'"$chain_name"'"' >&2
    exit 1
fi

declare -i chainid
chainid="$(get_config chainId)"
declare -r -i chainid

declare singleton
singleton="$(get_config safe.singleton)"
declare -r singleton
declare factory
factory="$(get_config safe.factory)"
declare -r factory
declare fallback
fallback="$(get_config safe.fallback)"
declare -r fallback
declare multicall
multicall="$(get_config safe.multiCall)"
declare -r multicall

if [[ $(cast keccak "$(cast code --rpc-url "$rpc_url" "$deployer")") != "$deployer_hash" ]] ; then
    echo 'Deterministic deployer does not exist; cannot proceed' >&2
    exit 1
fi

if [[ $(cast keccak "$(cast code --rpc-url "$rpc_url" "$singleton")") != "$singleton_hash" ]] ; then
    declare -r deploy_singleton=Yes
    echo 'Will deploy singleton' >&2
fi

if [[ $(cast keccak "$(cast code --rpc-url "$rpc_url" "$factory")") != "$factory_hash" ]] ; then
    declare -r deploy_factory=Yes
    echo 'Will deploy factory' >&2
fi

if [[ $(cast keccak "$(cast code --rpc-url "$rpc_url" "$fallback")") != "$fallback_hash" ]] ; then
    declare -r deploy_fallback=Yes
    echo 'Will deploy fallback' >&2
fi

if [[ $(cast keccak "$(cast code --rpc-url "$rpc_url" "$multicall")") != "$multicall_hash" ]] ; then
    declare -r deploy_multicall=Yes
    echo 'Will deploy multicall' >&2
fi

if [[ ${BROADCAST-no} = [Yy]es ]] ; then
    if [[ ${deploy_singleton-no} = [Yy]es ]] ; then
        cast send --gas-limit 30000000 --gas-price 10gwei --rpc-url http://127.0.0.1:1248/ --chain $chainid --from 0xEf37aD2BACD70119F141140f7B5E46Cd53a65fc4 --unlocked $(get_config extraFlags) 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7 "$singleton_init"
    fi
    if [[ ${deploy_factory-no} = [Yy]es ]] ; then
        cast send --gas-limit 30000000 --gas-price 10gwei --rpc-url http://127.0.0.1:1248/ --chain $chainid --from 0xEf37aD2BACD70119F141140f7B5E46Cd53a65fc4 --unlocked $(get_config extraFlags) 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7 "$factory_init"
    fi
    if [[ ${deploy_fallback-no} = [Yy]es ]] ; then
        cast send --gas-limit 30000000 --gas-price 10gwei --rpc-url http://127.0.0.1:1248/ --chain $chainid --from 0xEf37aD2BACD70119F141140f7B5E46Cd53a65fc4 --unlocked $(get_config extraFlags) 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7 "$fallback_init"
    fi
    if [[ ${deploy_multicall-no} = [Yy]es ]] ; then
        cast send --gas-limit 30000000 --gas-price 10gwei --rpc-url http://127.0.0.1:1248/ --chain $chainid --from 0xEf37aD2BACD70119F141140f7B5E46Cd53a65fc4 --unlocked $(get_config extraFlags) 0x914d7Fec6aaC8cd542e72Bca78B30650d45643d7 "$multicall_init"
    fi
fi

echo 'Done!' >&2
